if(view.stereographic == false, set(backtonormalfirst,true) );
if(backtonormalfirst, tween(view.fisheye, 0.0 ,distance(1.0, 0.25), easeoutquad, WAIT); );
set(view.pannini, true);
set(view.stereographic, true);
